  private void calc()
  {
    for(int i = 0; i < tm.getRowCount(); i++)
    {
      try
      {
        double res = JackPiClient.getData(tm.getDoubleAt(i, 0), tm.getDoubleAt(i, 1));

        tm.setValueAt(res, i, 2);
        //System.out.println("Value at: " + tm.getValueAt(i, 2));
        
      }
      catch (IOException ex)
      {
        JOptionPane.showMessageDialog(this, "Error!" + ex.getLocalizedMessage());
      }
      catch (Exception ex)
      {
      }
    }
    tm.fireTableDataChanged();
  }

  private void add()
  {
    
    tm.add((double)jSpinner1.getValue(), (double)jSpinner2.getValue());
    tm.fireTableDataChanged();
  }

